import React, { Component } from "react";
import NavbarCSS from "../../components/header/Header.css";
import Navbar from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import Carousel from "../../components/carousel/Carousel";
import { Link } from "react-router-dom";
import axios from "axios";

const Product = (props) => (




<div class="col-sm-6 col-md-4">
				<div class="thumbnail" >
					<img src="http://placehold.it/650x450&text=Galaxy S5" class="img-responsive"/>
					<div class="caption">
						<div class="row">
							<div class="col-md-6 col-xs-6">
								<h3>{props.product.name}</h3>
							</div>
							<div class="col-md-6 col-xs-6 price">
								<h3>
								<label>{props.product.cost}</label></h3>
							</div>
						</div>
						<div class="row">
							<div class="col-md-6">
								<a class="btn btn-primary btn-product"><span class="glyphicon glyphicon-thumbs-up"></span>Add To cart</a> 
							</div>
							<div class="col-md-6">
								<a href={"/view-product/" + props.product._id} class="btn btn-success btn-product"><span class="glyphicon glyphicon-shopping-cart"></span>More Details</a></div>
						</div>

						<p> </p>
					</div>
				</div>
			</div>

  
);

export default class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = { products: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ products: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return <Product product={currentProduct} key={currentProduct._id} />;
    });
  }

  render() {
    return (
      <div>
        <Navbar />
        <Carousel />
        <section class="section-name padding-y-sm">
          <div class="container">
            <header class="section-heading">
              <h3 class="section-title text-center my-5">Popular products</h3>
            </header>

            <div class="row">{this.productList()}</div>
          </div>
        </section>
        <Footer />
      </div>
    );
  }
}
