import React, { Component } from "react";
import NavbarCSS from "../../components/header/Header.css";
import Navbar from "../../components/header/Header";
import Footer from "../../components/footer/Footer";
import Carousel from "../../components/carousel/Carousel";
import { Link } from "react-router-dom";
import axios from "axios";

const Product = (props) => (




<div class="card" width="18rem">
  <img class="card-img-top" src="..." alt="Card image cap"/>
  <div class="card-body">
    <h5 class="card-title">{props.product.name}</h5><span>{props.product.cost}</span>
    <a href={"/view-product/" + props.product._id} class="btn btn-primary">Go somewhere</a>
  </div>
</div>

  
);

export default class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = { products: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ products: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  productList() {
    return this.state.products.map((currentProduct) => {
      return <Product product={currentProduct} key={currentProduct._id} />;
    });
  }

  render() {
    return (
      <div>
        <Navbar />
        <Carousel />
        <section class="section-name padding-y-sm">
          <div class="container">
            <header class="section-heading">
              <h3 class="section-title text-center my-5">Popular products</h3>
            </header>

            <div class="row">{this.productList()}</div>
          </div>
        </section>
        <Footer />
      </div>
    );
  }
}
