import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import Navbar from "../components/navbar.component";

const Review = (props) => (
  <tr>
    <td>{props.review.product_id}</td>
    <td>{props.review.review}</td>
    <td>
      <Link
        to={"/review/edit/" + props.review._id}
        className="btn btn-warning"
      >
        edit
      </Link>
    </td>
    <td>
      <a
        href="#"
        className="btn btn-danger"
        onClick={() => {
          props.deleteProduct(props.review._id);
        }}
      >
        delete
      </a>
    </td>
  </tr>
);

export default class ReviewsList extends Component {
  constructor(props) {
    super(props);

    this.deleteReview = this.deleteReview.bind(this);

    this.state = { reviews: [] };
  }

  componentDidMount() {
    axios
      .get("http://localhost:5000/products/")
      .then((response) => {
        this.setState({ reviews: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  deleteReview(id) {
    axios.delete("http://localhost:5000/reviews/" + id).then((response) => {
      console.log(response.data);
    });

    this.setState({
      products: this.state.reviews.filter((el) => el._id !== id),
    });
  }

  reviewList() {
    return this.state.reviews.map((currentReview) => {
      return (
        <Review
          review={currentReview}
          deleteReview={this.deleteReview}
          key={currentReview._id}
        />
      );
    });
  }
  render() {
    return (
      <div>
        <Navbar />
        <div className="px-5">
          <h3>Review List</h3>

          <table className="table table-striped table-bordered table-hover ">
            <thead className="thead-dark">
              <tr>
                <th width="10%">Product ID</th>
                <th width="10%">Review</th>
                <th colSpan="2" style={{ textAlign: "center", width: "10%" }}>
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>{this.ReviewsList()}</tbody>
          </table>

          <div>
            <a href="/Review/create/" className="btn btn-primary">
              Add Review
            </a>
          </div>
        </div>
      </div>
    );
  }
}
